(() => {
  // javascript/script.js
  gsap.registerPlugin(ScrollTrigger);
  var container = document.getElementById("container");
  gsap.to(container, {
    x: () => -(container.scrollWidth - document.documentElement.clientWidth) + "px",
    ease: "none",
    scrollTrigger: {
      trigger: container,
      invalidateOnRefresh: true,
      pin: true,
      scrub: 1,
      start: "top 80",
      end: () => "+=" + container.offsetWidth
    }
  });
  var large = document.querySelector(".large-child");
  gsap.to(large, {
    x: () => -(large.clientWidth + document.documentElement.clientWidth) + "px",
    ease: "none",
    scrollTrigger: {
      trigger: large,
      pin: true,
      start: "left left",
      end: () => "+=1000",
      scrub: 1,
      invalidateOnRefresh: true
    }
  });
  var pageContainer = document.querySelector(".container-this");
  var scroller = new LocomotiveScroll({
    el: pageContainer,
    smooth: true
  });
  scroller.on("scroll", ScrollTrigger.update);
  ScrollTrigger.scrollerProxy(pageContainer, {
    scrollTop(value) {
      return arguments.length ? scroller.scrollTo(value, 0, 0) : scroller.scroll.instance.scroll.y;
    },
    getBoundingClientRect() {
      return {
        right: 0,
        top: 0,
        width: window.innerWidth,
        height: window.innerHeight
      };
    },
    pinType: pageContainer.style.transform ? "transform" : "fixed"
  });
  var svgText = document.querySelector("#textOnPath1");
  var tl = gsap.timeline({
    scrollTrigger: {
      trigger: "#trigger",
      scroller: pageContainer,
      scrub: true,
      pin: true,
      start: "top 80",
      end: () => "+=500%",
      yoyo: true
    }
  });
  tl.to(svgText, {
    yoyo: true,
    attr: {
      startOffset: -2e3
    }
  }, 0);
  ScrollTrigger.addEventListener("refresh", () => scroller.update());
  ScrollTrigger.refresh();
})();
